query TransactionEdgeHeader {
  __typename
  searchTransactions(indexName: CALLS, query: "to:0x06012c8cf97BEaD5deAe237070F9587f8E7A266d") {
    edges {
      undo
      block {
        hash
        number
        header {
          coinbase
          transactionsRoot
          gasLimit
          gasUsed
          extraData
          difficulty
          parentHash
          timestamp
          stateRoot
        }
      }
    }
  }
}

query TransactionEdge {
  __typename
  searchTransactions(indexName: CALLS, query: "to:0x06012c8cf97BEaD5deAe237070F9587f8E7A266d") {
    edges {
      block {
        hash
        number
      }
      undo
    }
    pageInfo {
      endCursor
      startCursor
    }
  }
}

query TransactionOutcome {
  __typename
  transaction(hash: "0x3be3b44ae48a074d3b79e3054bb3b62b5c5e5a8fc2210cd1dc7c7932ae5addcd") {
    from
    to
    outcome
    status
  }
}

query Transaction {
  transaction(hash: "0x1798aefe0fe6f15abcaed3901474c1bd4303ba0fafe32d232e5121e29d63841e") {
